function request(e){$.ajax({url:"https://en.wikipedia.org/w/api.php?action=query&list=search&srsearch="+e+"&prop=info&inprop=url&utf8=&format=json",dataType:"jsonp",success:function(e){if(0===e.query.searchinfo.totalhits);else for(var t=0;t<e.query.search.length;t++)snippet[t]=e.query.search[t].snippet,title[t]=e.query.search[t].title,urlWiki[t]="https://en.wikipedia.org/wiki/"+e.query.search[t].title,$("#results").append('<article class="posts"><h2><a href="https://en.wikipedia.org/wiki/'+title+'">'+title[t]+"</a></h2><p>"+snippet[t]+"</p></article>")},error:function(){alert("Error retrieving search results, please refresh the page")}})}var snippet=[],title=[],urlWiki=[];$("input").on("keydown",function e(t){if(13===t.keyCode&&""!==e){var e=$("input").val();$("#results").html(""),request(e)}}),$(".result-btn").click(function(e){if(e.preventDefault(),""!==t){var t=$("input").val();$("#results").html(""),request(t)}});